import Head from 'next/head';
import { useEffect } from 'react';
import { XIcon } from '@heroicons/react/outline';
import Header from '../components/Header';
import Navbar from '../components/Navbar';
import Map from '../components/Map';
import styles from '../styles/Home.module.css';

const DEFAULT_CENTER = [3.315834, 101.559201];

export default function Home() {
	useEffect(() => {
		// var openmodal = document.querySelectorAll('.modal-open');
		// for (var i = 0; i < openmodal.length; i++) {
		// 	openmodal[i].addEventListener('click', function (event) {
		// 		event.preventDefault();
		// 		toggleModal();
		// 	});
		// }

		const overlay = document.querySelector('.modal-overlay');
		overlay.addEventListener('click', toggleModal);

		var closemodal = document.querySelectorAll('.modal-close');
		for (var i = 0; i < closemodal.length; i++) {
			closemodal[i].addEventListener('click', toggleModal);
		}

		document.onkeydown = function (evt) {
			evt = evt || window.event;
			var isEscape = false;
			if ('key' in evt) {
				isEscape = evt.key === 'Escape' || evt.key === 'Esc';
			} else {
				isEscape = evt.keyCode === 27;
			}
			if (isEscape && document.body.classList.contains('modal-active')) {
				toggleModal();
			}
		};

		function toggleModal() {
			const body = document.querySelector('body');
			const modal = document.querySelector('.modal');
			modal.classList.toggle('opacity-0');
			modal.classList.toggle('pointer-events-none');
			body.classList.toggle('modal-active');
		}
	});

	return (
		<div>
			<Head>
				<title>MyPutih</title>
			</Head>

			<main>
				<Header />
				<Navbar />
				<button
					onClick={toggleModal}
					className="modal-open bg-transparent border border-gray-500 hover:border-indigo-500 text-gray-500 hover:text-indigo-500 font-bold py-2 px-4 rounded-full">
					Open Modal
				</button>

				<div className="modal z-50 opacity-0 pointer-events-none fixed w-full h-full top-0 left-0 flex items-center justify-center">
					<div className="modal-overlay absolute w-full h-full bg-gray-900 opacity-50"></div>
					<div className="modal-container bg-white w-11/12 md:max-w-md mx-auto rounded shadow-lg z-50 overflow-y-auto">
						<div className="modal-content py-4 text-left px-6">
							<div className="flex justify-between items-center pb-3">
								<p className="text-2xl font-bold">Simple Modal!</p>
								<div className="modal-close cursor-pointer z-50">
									<XIcon className="h-5 w-5 text-gray-500" />
								</div>
							</div>

							<p>Modal content can go here</p>
							<p>...</p>
							<p>...</p>
							<p>...</p>
							<p>...</p>

							<div className="flex justify-end pt-2">
								<button className="px-4 bg-transparent p-3 rounded-lg text-indigo-500 hover:bg-gray-100 hover:text-indigo-400 mr-2">
									Action
								</button>
								<button className="modal-close px-4 bg-indigo-500 p-3 rounded-lg text-white hover:bg-indigo-400">
									Close
								</button>
							</div>
						</div>
					</div>
				</div>

				<Map className={styles.homeMap} center={DEFAULT_CENTER} zoom={14}>
					{({ TileLayer, Marker, Popup }) => (
						<>
							<TileLayer
								url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
								attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
							/>
							<Marker position={DEFAULT_CENTER}>
								<Popup>
									A pretty CSS3 popup. <br /> Easily customizable.
								</Popup>
							</Marker>
						</>
					)}
				</Map>
			</main>
		</div>
	);
}
